import React from 'react';
import './Testimonio.css';

class Testimonio extends React.Component {
	render() {
		return (
			<div className="contenedor-testimonio">
				<img className='imagen-testimonio'
					src={require(`../imagenes/testimonio-${this.props.imagen}.png`)}
					alt='foto perfil' />

				<div className='contenedor-texto-testimonio'>
					<p className='nombre-testimonio'><strong>{this.props.nombre}</strong> en {props.pais}</p>
					<p className='cargo-testimonio'>{this.props.cargo} en <strong>{props.empresa}</strong></p>
					<p className='texto-testominio'>{props.testimonio}</p>

				</div>

			</div>
		);

	}

}




export default Testimonio;
